    public async Task GetPedido()
    {
        pedido = await http.GetJsonAsync<Pedido>($"api/pedido/{pedidoId}");
        factura.Cliente = pedido.Cliente;
        factura.ClienteId = (int)pedido.ClienteId;
        factura.Domicilio = pedido.Domicilio;
        factura.Domicilio.Id = 0;
        factura.ListaPrecios = pedido.ListaPrecios;
        factura.ListaPreciosId = pedido.ListaPreciosId;
        factura.Vendedor = pedido.Vendedor;
        factura.VendedorId = pedido.VendedorId;
        factura.nombreCliente = pedido.nombreCliente;
        factura.celularCliente = pedido.celularCliente;
        factura.totalComprobante = pedido.total;
        factura.apellidoCliente = pedido.apellidoCliente;
        factura.emailCliente = pedido.emailCliente;
        factura.fechaEntrega = pedido.fechaEntrega;

        pedido.detallePedido.ForEach(x =>
        {
            factura.detallesFactura.Add(new DetalleFactura
            {
                ProductoPresentacion = x.ProductoPresentacion,
                ProductoPresentacionId = x.ProductoPresentacionId,
                precioUnitario = x.precioUnitario,
                cantidad = x.cantidad,
                costo = x.costo

            });
        });
        CalcularTotal();
        this.StateHasChanged();
    }
    public async Task ListarListas()
    {
        if (factura.ClienteId == 0)
        {
            listas = await http.GetJsonAsync<List<ListaPrecios>>($"api/ListaPrecios/ListaPrecioEcommerce?provinciaId={factura.Domicilio.ProvinciaId}&clienteId=0");

        }
        else
        {
            listas = await http.GetJsonAsync<List<ListaPrecios>>($"api/ListaPrecios/ListaPrecioEcommerce?provinciaId={factura.Domicilio.ProvinciaId}&clienteId={factura.ClienteId}");

        }

        if (listas.Count() > 0)
        {
            if (lista == null)
            {
                await seleccionarLista(factura.ListaPreciosId != 0 ? factura.ListaPreciosId : listas[0].Id);
                cargarProductos();
                await ActualizarMapa();

            }
        }
        else
        {
            lista = null;

        }
    }
