        }
    }



    public async Task EliminarProvincia(Provincia provincia)
    {
        provincia.habilitada = false;
        await http.PutJsonAsync($"api/provincia/{provincia.Id}", provincia);
        await ListarProvincias();
        this.provincia = null;

    }

    public async Task EliminarDepartamento(Departamento departamento)
